const INITAL_STATE = {
    isLogged:true,
    message: "",
    user: "",
    id: ""
}

export default function Auth(state= INITAL_STATE, action){

    switch(action.type){
        case 'LOG_IN':
           return{ ...state, isLogged: action.payload}
        case 'ERRO_LOGIN':
            return{ ...state, message: action.payload}
        case 'CLEAR_MESSAGE':
            return { ...state, message: ""}
        case 'LOG_OUT':
            return{ ...state, isLogged: action.payload}
        default:
            return state;
    }
}
