import { takeEvery, put, call, } from 'redux-saga/effects'; 
import axios from 'axios';



//CONJUNTO DE FUNÇÕES PARA VALIDAÇÃO DE USUÁRIO
function getUser(user){
    return axios({
        method: "GET",
        url:"http://localhost:3001/authenticate",
        data:{
            email:user.email,
            password: user.password
        }
    })
}

function* asyncLogIn(action){
    console.log(action);

    const response = yield call(getUser, action.payload);

    if(response.status === 200){
        yield put({ type: 'LOG_IN', payload: true});

    }else{
        alert("nao deu");
    }
}

export default function* root(){ 
    console.log("entrou no root");

    yield takeEvery('ASYNC_LOG_IN', asyncLogIn)

}
