import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Modal from '@material-ui/core/Modal';


import * as TaskActions from '../../actions/Task';
import './styles.css';

class Task extends Component {

  state={
    modal: false
  }

  editTask(id){

  }

  deleteTask(id){

  }


  handleClose(){
    this.setState({
      modal:false
    })
  }

  openModal(){
    this.setState({
      modal:true
    })
  }

  renderTasks(){
    return this.props.tasks.map(task => {
      return(
        <div id="card_task" key={task._id}>
          <div>{task.descricao}</div>
          <EditIcon id="editIcon" onClick={() => this.editTask(task._id)}/>
          <DeleteIcon id="deleteIcon" onClick={() => this.deleteTask(task._id)} />
          <button onClick={() => this.openModal()}>Modal</button>

        {/* TESTE DE MODAL */}
          <Modal
            aria-labelledby="simple-modal-title"
            aria-describedby="simple-modal-description"
            open={this.state.modal}
            onClose={this.handleClose()}
          >
            <div >
              <h2 id="simple-modal-title">Text in a modal</h2>
              <p id="simple-modal-description">
                Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
              </p>
              {/* <SimpleModal /> */}
            </div>
          </Modal>

        </div>
      )
    })
  }

  render() {
    return(
        <div className="container_tasks">
            {this.renderTasks()}
        </div>
    );
  }
}


const mapStateToProps = state => ({
  tasks: state.Task.tasks
});

const  mapDispatchToProps = dispatch =>
  bindActionCreators(TaskActions, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Task);
