export function listTasks(id){
    return{
        type: 'ASYNC_TASK_LIST',
        payload:id
    }
}

export function edit_task(id, descricao){
    return{
        type: 'ASYNC_EDIT_TASK',
        payload:{
            id,
            descricao
        }
    }
}

export function delete_task(id){
    return{
        type: 'ASYNC_DELETE_TASK',
        payload:{
            id
        }
    }
}
