export function listTasks(id){
    return{
        type: 'ASYNC_TASK_LIST',
        payload:id
    }
}

export function edit_task(id, descricao, user_id){
    return{
        type: 'ASYNC_EDIT_TASK',
        payload:{
            id,
            descricao,
            user_id
        }
    }
}
