import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Modal from '@material-ui/core/Modal';
import TextField from '@material-ui/core/TextField';


import * as TaskActions from '../../actions/Task';
import './styles.css';

class Task extends Component {

  state={
    modal: false,
    descricao:"",
    task_id:""
  }

  editTask(){

    const { descricao, task_id } = this.state;

    this.setState({
      modal:false
    });

    this.props.edit_task(task_id, descricao);

  }

  deleteTask(id){

  }


  handleClose(){
    this.setState({
      modal:false
    })
  }

  openModal(task){
    this.setState({
      modal:true,
      descricao: task.descricao,
      task_id:task._id
    })
  }


  renderTasks(){
    return this.props.tasks.map(task => {
      return(
        <div id="card_task" key={task._id}>
          <div>{task.descricao}</div>
          <EditIcon id="editIcon" onClick={() => this.openModal(task)}/>
          <DeleteIcon id="deleteIcon" onClick={() => this.deleteTask(task._id)} />
          <button onClick={() => this.openModal()}>Modal</button>



        </div>
      )
    })
  }


  alteraDescricao(descricao){
    this.setState({
      descricao
    })
  }

  render() {
    return(
        <div className="container_tasks">
          {this.renderTasks()}

          {/* MODAL */}
          <Modal
            className="edit_modal"
            open={this.state.modal}
            onClose={() => this.handleClose()}
          >
            <div id="div_modal"  >
              <h2 >Edit</h2>
              <TextField 
                placeholder="Descrição"
                value={this.state.descricao}
                onChange={e => this.alteraDescricao(e.target.value)}
              />
              <button onClick={() => this.editTask()}>Salvar</button>
            </div>
          </Modal>
        </div>
    );
  }
}


const mapStateToProps = state => ({
  tasks: state.Task.tasks
});

const  mapDispatchToProps = dispatch =>
  bindActionCreators(TaskActions, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Task);
