import { takeEvery, takeLatest, put, call } from 'redux-saga/effects'; 
import axios from 'axios';



//CONJUNTO DE FUNÇÕES PARA VALIDAÇÃO DE USUÁRIO
function getUser(user){
    console.log(user);

    return axios({
        method: 'POST',
        url:'http://localhost:3001/authenticate',
        data:{
            email:user.email,
            password: user.password
        }
    })
}

function* asyncLogIn(action){

    console.log("entrou na funcao do saga");
    try{
        const response = yield call(getUser, action.payload);
    console.log(response);

    yield put({ type: 'LOG_IN', payload: true});
    } catch(e){
        console.log("nao deu");

    }


    // if(response.status === 200){
    //     yield put({ type: 'LOG_IN', payload: true});

    // }else{
    //     alert("nao deu");
    //     console.log("nao deu");

    // }

}

export default function* root(){ 
    console.log("entrou no root");

    yield takeLatest('ASYNC_LOG_IN', asyncLogIn)

}
